mapper.insert:
INSERT INTO FEEDS(
    FEED_ID
    ,USER_ID1
    ,USER_ID2
    ,USER_ID3
    ,USER_ID4
    ,CONTEXT
) VALUES (
    #{feed_id}
    ,#{user_id1}
    ,#{user_id2, jdbcType=VARCHAR}
    ,#{user_id3, jdbcType=VARCHAR}
    ,#{user_id4, jdbcType=VARCHAR}
    ,#{context}
)

mapper.update:
UPDATE FEEDS SET
	CONTEXT = #{context}
WHERE
	FEED_ID = #{feed_id}

mapper.select:
SELECT FEED_ID ,USER_ID1 ,USER_ID2 ,USER_ID3 ,USER_ID4 ,CONTEXT ,INPUTDATE FROM FEEDS WHERE FEED_ID IN (
#{one.feed_id}
)
