mapper.update:
	UPDATE PAGE_LAYER SET
    	STRINGIFY = #{stringify}
	WHERE
    	ROOM_ID = #{room_Id}
    	AND
    	PAGE_NO = #{page_no}
    	AND
    	LAYER_NO = #{layer_no}

mapper.delete:
DELETE FROM
	PAGE_LAYER
WHERE
	ROOM_ID = #{room_Id}
	AND
    PAGE_NO = #{page_no}
	AND
    LAYER_NO = #{layer_no}
